import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { PostServiceService } from '../services/post/post-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent implements OnInit {

  constructor(private postservice : PostServiceService, private router : Router){}

  ngOnInit(): void {
    this.findAll();
  }


  posts : any[] = [];


  findAll()  {
    this.postservice.findAll().subscribe({
      next : (response) => {
          this.posts = response.post
      }, 

      error : (err) => console.log(err) 
    })
  }


  addPost(){
    this.router.navigate(["add-post"]);
  }


  edit(){
    this.router.navigate(["edit-post"]);
  }




}
<div class="container mx-auto px-4 sm:px-8">
    <div class="py-4">
        <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" (click)="addPost()">Add New Post</button>
    </div>
    <div class="py-4">
        <div class="flex flex-wrap -mx-4">
            <div class="w-full md:w-1/2 lg:w-1/3 px-4 mb-8" *ngFor="let post of posts">
                <div class="bg-white shadow-lg rounded-lg overflow-hidden">
                    <div class="p-4">
                        <div class="text-blue-500 text-md font-semibold">{{ post.category_id }}</div>
                        <h2 class="text-lg font-semibold mb-2">{{ post.title }}</h2>
                        <p class="text-gray-700 mb-4">{{ post.content | slice:0:100 }}...</p>
                        <div class="flex items-center justify-between">
                            <div class="text-sm text-gray-600">Published on {{ post.publish_date}}</div>
                            <div class="text-sm text-gray-600">By User {{ post.user_id }}</div>
                        </div>
                    </div>
                    <div class="p-4 flex justify-between border-t border-gray-200">
                        <button (click)="edit" class="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-4 rounded">Update</button>
                        <button class="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-4 rounded" >Delete</button>
                        <a href="#" class="text-indigo-600 hover:text-indigo-900 font-semibold">Read More</a>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
