import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { EventServiceService } from "src/app/services/event/event-service.service";

@Component({
  selector: "app-event-details",
  templateUrl: "./event-details.component.html",
  styleUrls: ["./event-details.component.css"],
})
export class EventDetailsComponent implements OnInit {
  // Assuming `event` is an object, initialize as null (and potentially specify a more precise type)
  event: any = null;

  constructor(
    private route: ActivatedRoute,
    private eventService: EventServiceService // Adjusted naming convention
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      const eventId = +params["id"];
      if (eventId) {
        this.findEventById(eventId); // Adjusted naming convention
      }
    });
  }

  findEventById(id: number): void {
    this.eventService.findEventById(id).subscribe({ // Adjusted naming convention
      next: (response) => {
        this.event = response.event;
        console.log(this.event);
      },
      error: (error) => console.log(error),
    });
  }
}
