import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { TagsResponse } from 'src/app/shared/model/tags-response';

@Injectable({
  providedIn: 'root'
})
export class TagsServiceService {

  constructor(private http : HttpClient) { }
  UrlApi : string = "http://127.0.0.1:8000/api/tags";


  addtag(tags : TagsResponse) : Observable<TagsResponse>{
    return  this.http.post<TagsResponse>(this.UrlApi , tags);
  }

  deletetag(id : number) : Observable<void>{
    return this.http.delete()
  }







}
