import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from "@angular/router";
import { EventServiceService } from 'src/app/services/event/event-service.service';

@Component({
  selector: 'app-event-details',
  templateUrl: './event-details.component.html',
  styleUrls: ['./event-details.component.css']
})
export class EventDetailsComponent implements OnInit{


  constructor(private route: ActivatedRoute , private eventservice : EventServiceService) { }


  ngOnInit(): void {
      this.route.params.subscribe(params => {
        const eventId = +params['id']; 
        if (eventId) {
          console.log(eventId);
        }
      }
  }



  findeventbyid(){
    this.eventservice.FindEventById(1).subscribe(data => {
      console.log(data);
    }
  }

}
