import { Component, OnInit } from "@angular/core";
import { TagsServiceService } from "../services/tags/tags-service.service";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { TagsResponse } from "../shared/model/tags-response";

@Component({
  selector: "app-tags",
  templateUrl: "./tags.component.html",
  styleUrls: ["./tags.component.css"],
})
export class TagsComponent implements OnInit {
  TagsForm: FormGroup;

  message: String = "";

  constructor(private tagservice: TagsServiceService, private fb: FormBuilder) {
    this.TagsForm = this.fb.group({
      name: ["", Validators.required],
    });
  }

  tags: any[] = [];
  ngOnInit(): void {
    this.findAll
  }

  save() {
    if (this.TagsForm.valid) {
      this.tagservice.save(this.TagsForm.value).subscribe({
        next: () => {
          this.message = "tags added successfully";
          this.TagsForm.reset();
        },

        error: (err) => {
          console.log(err);
        },
      });
    }
  }

  delete(id: number): void {
    this.tagservice.delete(id).subscribe({
      next: () => console.log("tags deleted"),
      error: (err) => console.log(err),
    });
  }

  findAll() {
    this.tagservice.findAll().subscribe({
      next: (data) => (this.tags = data),
      error: (err) => console.error(err),
    });
  }

  update(id: number) {
    this.tagservice.update(id, this.TagsForm.value).subscribe({
      next: (category) => {
        console.log("category updated", category);
      },

      error: (err) => console.log(err),
    });
  }
}



<div class="container mx-auto px-4 py-8">
    <h2 class="text-2xl font-semibold mb-6">Add New Category</h2>
    <form [formGroup]="TagsForm" (ngSubmit)="save()" class="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
      <div class="mb-4">
        <label for="name" class="block text-gray-700 text-sm font-bold mb-2">Name</label>
        <input formControlName="name" type="text" id="name" placeholder="Enter category name" class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline">
      </div>

      <button type="submit" [disabled]="!TagsForm.valid" class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline disabled:opacity-50 disabled:cursor-not-allowed">
        Submit
      </button>
    </form>

    <p *ngIf="message" class="mt-4 text-center text-sm text-green-500">{{message}}</p>
</div>




<table class="min-w-full leading-normal">
  <thead>
    <tr>
      <th class="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider">
        ID
      </th>
      <th class="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider">
        Name
      </th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let tag of tags">
      <td class="px-5 py-5 border-b border-gray-200 bg-white text-sm">
        {{ tag.id }}
      </td>
      <td class="px-5 py-5 border-b border-gray-200 bg-white text-sm">
        {{ tag.name }}
      </td>
    </tr>
  </tbody>
</table>
