import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Observable } from "rxjs";
import { categoryResponse } from "src/app/shared/model/category-response";
import { TagsResponse } from "src/app/shared/model/tags-response";

@Injectable({
  providedIn: "root",
})
export class TagsServiceService {
  constructor(private http: HttpClient) {}
  UrlApi: string = "http://127.0.0.1:8000/api/tags";

  save(tags: TagsResponse): Observable<TagsResponse> {
    return this.http.post<TagsResponse>(this.UrlApi, tags);
  }

  delete(id: number): Observable<void> {
    return this.http.delete<void>(`${this.UrlApi}/${id}`);
  }

  findById(id: number): Observable<TagsResponse> {
    return this.http.get<TagsResponse>(`${this.UrlApi}/${id}`);
  }

  findAll(): Observable<TagsResponse[]> {
    return this.http.get<TagsResponse[]>(`${this.UrlApi}`);
  }


