import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { CategoryServiceService } from '../services/category/category-service.service';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit {
  categoryForm: FormGroup;
  message: string = '';

  constructor(
    private categoryService: CategoryServiceService,
    private fb: FormBuilder
  ) {
    this.categoryForm = this.fb.group({
      name: ['', Validators.required],
      description: ['', Validators.required]
    });
  }


  categories: any[] = [];

  ngOnInit(): void {
    this.findAll();
  }


  findAll(){
    this.categoryService.findAll().subscribe({ 
        next: (data) => {
          this.categories = data
        console.log(this.categories);
        },

        error: (err) => console.log(err),
    });
}



  add() {
    if (this.categoryForm.valid) {
      this.categoryService.save(this.categoryForm.value).subscribe({
        next: () => {
          this.message = 'Category added successfully!';
          this.categoryForm.reset(); 
        },
        error: (err) => {
          console.error(err);
          this.message = 'Failed to add category. Please try again.';
        }
      });
    }
  }
}



<div class="container mx-auto px-4 py-8">
    <h2 class="text-2xl font-semibold mb-6">Add New Category</h2>
    <form [formGroup]="categoryForm" (ngSubmit)="add()" class="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
      <div class="mb-4">
        <label for="name" class="block text-gray-700 text-sm font-bold mb-2">Name</label>
        <input formControlName="name" type="text" id="name" placeholder="Enter category name" class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline">
      </div>
      <div class="mb-6">
        <label for="description" class="block text-gray-700 text-sm font-bold mb-2">Description</label>
        <textarea formControlName="description" id="description" placeholder="Enter category description" class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline" rows="3"></textarea>
      </div>
      <button type="submit" [disabled]="!categoryForm.valid" class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline disabled:opacity-50 disabled:cursor-not-allowed">
        Submit
      </button>
    </form>
    <p *ngIf="message" class="mt-4 text-center text-sm text-green-500">{{message}}</p>
</div>



<table class="min-w-full leading-normal">
  <thead>
    <tr>
      <th class="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider">
        ID
      </th>
      <th class="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider">
        Name
      </th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let category of categories">
      <td class="px-5 py-5 border-b border-gray-200 bg-white text-sm">
        {{ category.id }}
      </td>
      <td class="px-5 py-5 border-b border-gray-200 bg-white text-sm">
        {{ category.name }}
      </td>
    </tr>
  </tbody>
</table>
